import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { createStore, applyMiddleware } from 'redux';
// import MainNavigation from './Navigations/MainNavigation';
import { Provider } from 'react-redux';
import reducer from './reducers';
import thunk from 'redux-thunk';
import logger from 'redux-logger';

import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import  DeckList  from './components/DeckList';
import AddDeck from './components/AddDeck';
import AddCard from './components/AddCard';
import { createStackNavigator } from '@react-navigation/stack';
import DeckDetail from './components/DeckDetail';
import StartQuiz from './components/Quiz';

import { Ionicons } from '@expo/vector-icons';

const store = createStore(
  reducer,
  applyMiddleware(thunk,logger)
);


const Tabs = createBottomTabNavigator(
  {
    DeckList: {
      screen: DeckList,
      navigationOptions: {
        title: 'FlashCards',
        tabBarLabel: 'Decks',
        tabBarIcon: ({ tintColor }) => <Ionicons name={Platform.OS === 'ios' ? 'ios-list' : 'md-list'} size={30} color={tintColor}/>
      }
    },
    NewDeck: {
      screen: AddDeck,
      navigationOptions: {
        title: 'FlashCards',
        tabBarLabel: 'Add Deck',
        tabBarIcon: ({ tintColor }) => <Ionicons name={Platform.OS === 'ios' ? 'ios-add-circle-outline' : 'md-add-circle-outline'} size={30} color={tintColor}/>
      }
    }
  },
    {
    tabBarOptions: {
      activeTintColor: lightPurp,
      style: {
        backgroundColor: white,
      },
    }
  }
);

Tabs.navigationOptions = ({ navigation }) => {
const { routeName } = navigation.state.routes[navigation.state.index];

const headerTitle = routeName;
return {
  headerTitle,
};
};

const MainNavigation = createStackNavigator({
    DeckList: {
      screen: Tabs,
    },
    DeckDetail: {
      screen: DeckDetail,
      navigationOptions: {
        headerTintColor: black,
        headerStyle: {
          backgroundColor: white
        }
      }
    },
    AddCard: {
      screen: AddCard,
      navigationOptions: {
        headerTintColor: black,
        headerStyle: {
          backgroundColor: white
        }
      }
    },
    StartQuiz: {
      screen: StartQuiz,
      navigationOptions: {
        headerTintColor: black,
        headerStyle: {
          backgroundColor: white
        }
      },
    },
