import {Button, Card, Paragraph} from 'react-native-paper';

export default function DeckDetail(props) {
  const params = props.route.params;
  const {id} = params;
  console.log("My params",params)
  // const cardDeck = useSelector(state => decks.decks[title]);
  // const numberOfCards = cardDeck.questions.length;
  return (
    <View style={{flex: 1}}>


      <Card mode="elevated">
        <Card.Title title={title} />

        {/* <Deck id={title} /> */}
        {/* <Card.Content>
          <Paragraph>{numberOfCards} cards</Paragraph>
        </Card.Content> */}
      </Card>


      <View style={styles.buttonContainer}>
        <View style={styles.buttonSpacing}>
          <Button
            icon="plus"
            onPress={() => {
              props.navigation.navigate('AddCard', {title});
            }}>
            Add Card
          </Button>
        </View>
        <View style={styles.buttonSpacing}>
          <Button
            icon="plus"
            title="Start Quiz"
            onPress={() => {
              props.navigation.navigate('Quiz', {title});
            }}>
            Start Quiz
          </Button>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
