import React, { Component } from 'react';
import {ScrollView,View,Text,StyleSheet,TouchableOpacity} from 'react-native';
import { connect } from 'react-redux';
import Deck from './Deck';
import { blue, orange } from '../utilities/colors';
import { handleInitialData } from '../actions/action';

export class DeckList extends Component {

  componentDidMount() {
    this.props.handleInitialData();
  }

  render() {
    const { decks, navigation } = this.props;

    console.log("We are here decks")
    console.log("My data:",decks)

    return (
      <ScrollView style={styles.container}>

        <Text style={styles.title}>Udacity Mobile Flashcards</Text>

        {
        this.props.de === null ? "Data Is Null" : Object.values(decks).map(deck => {

          return (
            <TouchableOpacity
              key={deck.title}
              onPress={() =>
                navigation.navigate('DeckDetail', { title: deck.title })
              }
            >
              <Deck id={deck.title} />
            </TouchableOpacity>
          );
        })};


        <View style={{ marginBottom: 30 }} />

      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 16,
    paddingLeft: 16,
    paddingRight: 16,
    paddingBottom: 16,
    backgroundColor: blue
  },
  title: {
    fontSize: 40,
    textAlign: 'center',
    marginBottom: 16,
    color: orange
  }
});

const mapStateToProps = (state) => ({ decks: state });

const mapDispatchToProps = (dispatch) => (
    {
        handleInitialData: () => dispatch(handleInitialData()),
    }
)

export default connect(mapStateToProps,mapDispatchToProps)(DeckList);
