import React from "react";
import { getDeck } from "../utilities/api";
import {Button, Text, View} from "react-native";
import Styles from "../utilities/styles";
import { setLocalNotification,clearLocalNotification } from "../utilities/helpers";

export default class StartQuiz extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            questions: null,
            correct: 0,
            indexAt: 0,
            showQuestion: true,
        }
    }

    componentDidMount() {
        const { route} = this.props;

        const deckID = route.params.deck.title;

        getDeck(deckID).then((deckk) => {

                const questions = deckk.questions;

                this.setState({questions: questions});
            }
        );
    }

    render() {
        if(this.state.questions === null || this.state.questions === undefined) 
        {
            return null;
        } 
        else if(this.state.questions.length === 0) 
        {
            return (
            <View style={Styles.VerticalAlignCenter}>
              <Text style={Styles.deckTexts}>There is no question card in the current deck...</Text>
            </View>);
        } 
        else if (this.state.questions.length !== this.state.indexAt) 
        {
            const question = this.state.questions[this.state.indexAt];
            return (
                <View style={Styles.VerticalAlignCenter}>
                    <Text style={Styles.deckTexts}>
                        Your Progress {this.state.indexAt+1} of {this.state.questions.length}
                    </Text>



                    <Text style={Styles.deckTexts}>
                        {this.state.showQuestion ? question.question : question.answer}
                    </Text>
                    <Button style={Styles.subButton}
                        title={this.state.showQuestion ? 'Show Answer' : 'Show Question'}
                        onPress={this.flipQA}
                    />

                    <View style={Styles.VerticalAlignCenter}>
                      {displayQuestion ? (
                        <Text style={Styles.deckTexts}>{selectedQuestion.question}</Text>
                      ) : (
                        <Text style={Styles.deckTexts}>{selectedQuestion.answer}</Text>
                      )}
                      <TouchableOpacity
                        onPress={() => setDisplayQuestion(!displayQuestion)}
                      >
                        {displayQuestion ? (
                          <Text style={Styles.subButton}>Answer</Text>
                        ) : (
                          <Text style={Styles.subButton}>question</Text>
                        )}
                      </TouchableOpacity>
                    </View>



                    <Button
                        title={'Correct'}
                        onPress={this.markAsCorrect}
                    />
                    <Button
                        title={'Incorrect'}
                        onPress={this.pushIncorrect}
                    />
                </View>
            )
        } 
        else {
            return (

                <View>
                    <Text>
                        You have successfully completed the deck questions!
                    </Text>
                    <Text>
                        Score: You have answered {Math.round(((this.state.correct)/this.state.questions.length)*100)}% Correct!
                    </Text>
                    <Button
                        title={'Restart Quiz'}
                        onPress={() => {

                            this.setState({
                                correct: 0,
                                indexAt: 0,
                                showQuestion: true,
                            });
                            this.Notify;
                        }}
                    />
                    <Button
                        title={'Back to Deck'}
                        onPress={() => {
                            this.props.navigation.navigate('DeckDetail', {
                                deck: this.props.route.params.deck,
                            });
                        }}
                    />
                </View>
            );
        }
    }

    flipQA = () => {
        this.setState(prevState => ({
            showQuestion: !prevState.showQuestion,
            correct: prevState.correct,
            indexAt: prevState.indexAt,
            questions: prevState.questions,
        }));
    }


    markAsCorrect = () => {
        this.setState(prevState => ({
            correct: prevState.correct+1,
            indexAt: prevState.indexAt+1,
            questions: prevState.questions,
            showQuestion: true,
        }));
    }

    pushIncorrect = () => {
        this.setState(prevState => ({
            indexAt: prevState.indexAt+1,
            showQuestion: true,
            correct: prevState.correct,
            questions: prevState.questions,
        }));
    }

  Notify = () => {
    clearLocalNotification();
    setLocalNotification();
    console.log("Notifications set")
  }
}
